mapper.select:
SELECT

FROM app_video_item a
WHERE a.id = #{id}

SELECT

FROM app_video_item a

    a.is_del = ${DEL_NORMAL}

        AND (a.`title` LIKE CONCAT('%',#{keyWord},'%')
        OR a.label LIKE CONCAT('%',#{keyWord},'%'))


        AND a.`on` = #{on}


        AND a.video_id = #{videoId}


        AND a.title = #{title}


        AND a.type = #{type}

mapper.update:
UPDATE app_video_item
title = #{title}, label = #{label}, url = #{url}, `on` = #{on}, img = #{img}, type = #{type}, prefix_time = #{prefixTime}, suffix_time = #{suffixTime}, intro = #{intro}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks},
WHERE id = #{id}
