mapper.select:
SELECT
FROM sys_role a WHERE a.is_del = ${DEL_NORMAL}
and (a.`name` like concat('%',#{keyword},'%') OR a.enname like concat('%',#{keyword},'%'))

mapper.update:
UPDATE sys_role
name = #{name}, enname = #{enname}, type = #{type}, useable = #{useable}, data_scope = #{dataScope}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks},
where id = #{id}
